//       <Board cells={board} onClick={handleClick}></Board>
//       {winner && <div className="game-winner">Winner is {winner}</div>}
//       <button className="button" onClick={handleResetGame}>
//         Reset game
//       </button>
//     </div>
//   );
// };

//=============================== TIC TAC TOE WITH USE REDUCER ==========================//
const initialState = {
  board: Array(9).fill(null),
  xIsNext: true,
};
const action = {
  type: "CLICK",
  payload: "",
};

const reducer = (state, action) => {
  switch (action.type) {
    case "CLICK": {
      const { board, xIsNext } = state;
      const nextState = JSON.parse(JSON.stringify(state)); // next state này clone ra để set giá trị, ko gây ảnh hưởng giá trị ban đầu
      const { index, winner } = action.payload;
      if (winner || board[index]) return state;

      console.log(nextState);
      nextState.board[index] = xIsNext ? "X" : "O";
      nextState.xIsNext = !xIsNext; // dòng này để set cái bước tiếp theo khác với giá trị bước trước đó <=> "X" != "O"

      return nextState;
    }

    case "RESET": {
      const resetState = JSON.parse(JSON.stringify(state));
      resetState.board = Array(9).fill(null); //set cho 9 thằng về null => giao diện trắng
      resetState.xIsNext = true; // set cho bước đi sau khi reset có thể click đc
      return resetState;
    }
    default:
      throw new Error("invalid value");
  }
};

const Game = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const winner = CalculatedWinner(state.board);

  const handleOnclick = (index) => {
    dispatch({
      type: "CLICK",
      payload: {
        index,
        winner,
      },
    });
  };

  const handleResetGame = () => {
    dispatch({
      type: "RESET",
      payload: {
        state,
      },
    });
  };
  return (
    <div className="">
      <Board cells={state.board} onClick={handleOnclick}></Board>
      {winner && <div className="game-winner">Winner is {winner}</div>}
      <button className="button" onClick={handleResetGame}>
        Reset game
      </button>
    </div>
  );
};
export default Game;
